import React, { Component } from 'react';
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';

class Blog extends Component {

    state = {
        title: '',
        game: '',
        platform: '',
        score: null,
        text: '',
        blog: true,
        review: false,
        walkthrough: false
    }

    constructor(props) {
        super(props)
        this.handleChange = this.handleChange.bind(this)
      }
     
      handleChange(value) {
        this.setState({ text: value })
      }

      handleInput = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        this.setState({ [name]: value }, () => { console.log(name, value) });
      }

    render() {
        return (
            <div className="container Blog">
                <div className="row justify-content-center">
                    <div className="col-10">
                        <form>
                            <div className="form-group">
                                <label htmlFor="title">Title</label>
                                <input name="title" onChange={this.handleInput} type="title" className="form-control" id="postTitle" aria-describedby="postTitle" placeholder="Enter a title for your post" />
                            </div>
                            <div className="form-group">
                                <label htmlFor="game">Game</label>
                                <input type="game" name="game" onChange={this.handleInput} className="form-control" id="postGame" placeholder="Enter the game's title" />
                            </div>
                            <div className="form-group">
                                <label htmlFor="game">Platform</label>
                                <input type="platform" name="platform" onChange={this.handleInput} className="form-control" id="postPlatform" placeholder="What are you playing on?" />
                            </div>
                            <div className="form-group">
                                <label htmlFor="content">Content</label>
                                <ReactQuill name="text" onChange={this.handleInput} />
                            </div>
                            <button type="submit" className="btn submitBtn">Create Post</button>
                        </form>

                    </div>
                </div>
            </div>
        );
    }
}

export default Blog;
